            /// <summary>Fast, mostly negative check to skip or proceed with interpretation.
            /// Depending on the context you may avoid calling it because you know the interpreted expression beforehand,</summary>
            [MethodImpl(MethodImplOptions.AggressiveInlining)]
            public static bool IsCandidateForInterpretation(Expression expr)
            {
                var nodeType = expr.NodeType;
                return
                    nodeType == ExpressionType.Constant |
                    nodeType == ExpressionType.Default |
                    nodeType == ExpressionType.Convert |
                    nodeType == ExpressionType.Not |
                    nodeType == ExpressionType.Negate |
                    expr is BinaryExpression;
            }

#if INTERPRETATION_DIAGNOSTICS
            public static readonly System.Collections.Concurrent.ConcurrentStack<string> UsedInTests = new();

            [UnconditionalSuppressMessage("Trimming", "IL2026:Members annotated with 'RequiresUnreferencedCodeAttribute' require dynamic access otherwise can break functionality when trimming application code", Justification = "Used in diagnostics only")]
            [UnconditionalSuppressMessage("Trimming", "IL2075:Members annotated with 'RequiresUnreferencedCodeAttribute' require dynamic access otherwise can break functionality when trimming application code", Justification = "Used in diagnostics only")]
            private static void CollectCallingTestName()
            {
                var stackTrace = new StackTrace();
                var frames = stackTrace.GetFrames();

                // Skip this method and its immediate caller, and start from the outer callers
                var found = false;
                for (int i = 3; i < frames.Length; ++i)
                {
                    var frame = frames[i];
                    var method = frame.GetMethod();
                    var type = method?.DeclaringType;
                    if (type == null)
                        continue;

                    var ifaces = type.GetInterfaces();
                    if (ifaces.Length == 0)
                        continue;

                    foreach (var iface in ifaces)
                        if (iface.Name.Contains("Test"))
                        {
                            found = true;
                            break;
                        }

                    if (found)
                    {
                        found = true;
                        Console.WriteLine($"Interpretation in: {type.Name}.{method.Name}");
                        UsedInTests.Push($"{type.Name}.{method.Name}");
                        break; // collect the first found thing in stack trace
                    }
                }

                if (!found)
                {
                    var methodTrace = string.Join("; ", frames.Skip(3).Select(f => f.GetMethod().Name).ToArray());
                    Console.WriteLine($"Interpretation in: not found in stack trace: {methodTrace}");
                    UsedInTests.Push($"Interpretation in: not found in stack trace: {methodTrace}");
                }
            }
#endif

            /// <summary>Wraps `TryInterpretPrimitive` in the try catch block.
            /// In case of exception FEC will emit the whole computation to throw exception in the invocation phase</summary>
            public static bool TryInterpret(out object result, Expression expr)
            {
                Debug.Assert(expr.Type.IsPrimitive);
                try
                {
                    UValue value = default;
                    if (TryInterpretPrimitive(ref value, expr))
                    {
#if INTERPRETATION_DIAGNOSTICS
                        CollectCallingTestName();
#endif
                        result = BoxToObject(ref value);
                        return true;
                    }
                }
                catch
                {
                    // ignore exception and return the false and rethrow the exception in the invocation time
                }
                result = null;
                return false;
            }

            /// <summary>Wraps `TryInterpretPrimitive` in the try catch block.
            /// In case of exception FEC will emit the whole computation to throw exception in the invocation phase</summary>
            public static bool TryInterpret_new(out object result, Expression expr)
            {
                var type = expr.Type;
                Debug.Assert(type.IsPrimitive);

                result = null;
                try
                {
                    if (type == typeof(bool))
                    {
                        var boolResult = false;
                        var ok = TryInterpretBool(ref boolResult, expr, expr.NodeType);
#if INTERPRETATION_DIAGNOSTICS
                        if (ok) CollectCallingTestName();
#endif
                        result = boolResult;
                        return ok;
                    }
                    if (type == typeof(decimal))
                    {
                        decimal decimalResult = default;
                        var ok = TryInterpretDecimal(ref decimalResult, expr, expr.NodeType);
#if INTERPRETATION_DIAGNOSTICS
                        if (ok) CollectCallingTestName();
#endif
                        result = ok ? decimalResult : null; // boxing
                        return ok;
                    }
                    {
                        PValue pResult = default;
                        var ok = TryInterpretPrimitiveValue(ref pResult, expr, expr.NodeType);
#if INTERPRETATION_DIAGNOSTICS
                        if (ok) CollectCallingTestName();
#endif
                        result = ok ? BoxToObject_new(ref pResult) : null;
                        return ok;
                    }
                }
                catch
                {
                    // ignore exception and return the false and rethrow the exception in the invocation time
                    return false;
                }
            }

            /// <summary>Wraps `TryInterpretPrimitive` in the try catch block.
            /// In case of exception FEC will emit the whole computation to throw exception in the invocation phase</summary>
            public static bool TryInterpretBool(out bool result, Expression expr)
            {
                Debug.Assert(expr.Type.IsPrimitive);
                try
                {
                    UValue value = default;
                    if (TryInterpretPrimitive(ref value, expr))
                    {
#if INTERPRETATION_DIAGNOSTICS
                        CollectCallingTestName();
#endif
                        Debug.Assert(value.Code == TypeCode.Boolean);
                        result = value.BooleanValue;
                        return true;
                    }
                }
                catch
                {
                    // ignore exception and return the false and rethrow the exception in the invocation time
                }
                result = false;
                return false;
            }

            /// <summary>Wraps `TryInterpretPrimitive` in the try catch block.
            /// In case of exception FEC will emit the whole computation to throw exception in the invocation phase</summary>
            public static bool TryInterpretBool_new(out bool result, Expression expr)
            {
                Debug.Assert(expr.Type.IsPrimitive);
                result = false;
                try
                {
                    var ok = TryInterpretBool(ref result, expr, expr.NodeType);
#if INTERPRETATION_DIAGNOSTICS
                    if (ok) CollectCallingTestName();
#endif
                    return ok;
                }
                catch
                {
                    // ignore exception and return the false and rethrow the exception in the invocation time
                    return false;
                }
            }

            /// <summary>Tries to interpret the expression of the Primitive type of Constant, Convert, Logical, Comparison, Arithmetic.</summary>
            internal static bool TryInterpretPrimitive(ref UValue value, Expression expr)
            {
                // The order of the checks for the type of the expression is deliberate,
                // because we are starting with complex expressions first. 
                // And for the simplest ones like a Constant? the method may not be even called.
                // Instead, the Constant check and interpretation may be done inline.

                var nodeType = expr.NodeType;
                if (nodeType == ExpressionType.Not)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        SetBoolean(ref value, (bool)co.Value);
                    }
                    else if (!TryInterpretPrimitive(ref value, operandExpr))
                        return false;
                    Debug.Assert(value.Code == TypeCode.Boolean);
                    value.BooleanValue = !value.BooleanValue;
                    return true;
                }

                if (nodeType == ExpressionType.AndAlso |
                    nodeType == ExpressionType.OrElse)
                {
                    var binaryExpr = (BinaryExpression)expr;

                    // Interpreting the left part as the first candidate for the result
                    var left = binaryExpr.Left;
                    if (left is ConstantExpression lc)
                    {
#if LIGHT_EXPRESSION
                        if (lc.RefField != null) return false;
#endif
                        SetBoolean(ref value, (bool)lc.Value);
                    }
                    else if (!TryInterpretPrimitive(ref value, left))
                        return false;
                    Debug.Assert(value.Code == TypeCode.Boolean);

                    // Short circuit the interpretation, because this is an actual logic of these logical operations
                    if (value.BooleanValue & nodeType == ExpressionType.OrElse ||
                        !value.BooleanValue & nodeType == ExpressionType.AndAlso)
                        return true;

                    // If the first part is not enough to decide of the expression result, go right
                    var right = binaryExpr.Right;
                    if (right is ConstantExpression rc)
                    {
#if LIGHT_EXPRESSION
                        if (rc.RefField != null) return false;
#endif
                        value.BooleanValue = (bool)rc.Value;
                        return true;
                    }
                    return TryInterpretPrimitive(ref value, right); // todo: @perf avoid recursion by assigning the `expr = right`
                }

                var isComparison = IsComparison(nodeType);
                if (isComparison || IsArithmeticBinary(nodeType))
                {
                    var binaryExpr = (BinaryExpression)expr;

                    // Interpreting left part
                    var left = binaryExpr.Left;
                    if (left is ConstantExpression lc)
                    {
#if LIGHT_EXPRESSION
                        if (lc.RefField != null) return false;
#endif
                        UnboxToValue(ref value, lc.Value);
                    }
                    else if (!TryInterpretPrimitive(ref value, left))
                        return false;

                    // Interpreting right part
                    var right = binaryExpr.Right;
                    UValue rightVal = default;
                    if (right is ConstantExpression rc)
                    {
#if LIGHT_EXPRESSION
                        if (rc.RefField != null) return false;
#endif
                        UnboxToValue(ref rightVal, rc.Value);
                    }
                    else if (!TryInterpretPrimitive(ref rightVal, right))
                        return false;

                    if (isComparison)
                        DoComparison(ref value, ref rightVal, nodeType);
                    else
                        DoArithmetic(ref value, ref rightVal, nodeType);
                    return true;
                }

                if (nodeType == ExpressionType.Negate)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        UnboxToValue(ref value, co.Value);
                    }
                    else if (!TryInterpretPrimitive(ref value, operandExpr))
                        return false;

                    DoNegate(ref value);
                    return true;
                }

                if (expr is ConstantExpression constExpr)
                {
#if LIGHT_EXPRESSION
                    if (constExpr.RefField != null) return false;
#endif
                    UnboxToValue(ref value, constExpr.Value);
                    return true;
                }

                if (nodeType == ExpressionType.Default)
                {
                    SetToZero(ref value, Type.GetTypeCode(expr.Type));
                    return true;
                }

                if (nodeType == ExpressionType.Convert)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        UnboxToValue(ref value, co.Value);
                    }
                    else if (!TryInterpretPrimitive(ref value, operandExpr))
                        return false;

                    var exprType = expr.Type;
                    if (operandExpr.Type != exprType)
                        DoConvert(ref value, Type.GetTypeCode(exprType));
                    return true;
                }
                return false;
            }

            /// <summary>Tries to interpret the expression of the Primitive type of Constant, Convert, Logical, Comparison, Arithmetic.</summary>
            internal static bool TryInterpretBool(ref bool boolResult, Expression expr, ExpressionType nodeType)
            {
                // what operations are supported, to get the boolean result?
                // yes: not, logical, comparison, default
                // not: negate, arithmetic, convert to bool
                if (nodeType == ExpressionType.Not)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        boolResult = (bool)co.Value;
                    }
                    else if (!TryInterpretBool(ref boolResult, operandExpr, operandExpr.NodeType))
                        return false;

                    boolResult = !boolResult;
                    return true;
                }

                if (nodeType == ExpressionType.AndAlso |
                    nodeType == ExpressionType.OrElse)
                {
                    var binaryExpr = (BinaryExpression)expr;

                    // Interpreting the left part as the first candidate for the result
                    var left = binaryExpr.Left;
                    if (left is ConstantExpression lc)
                    {
#if LIGHT_EXPRESSION
                        if (lc.RefField != null) return false;
#endif
                        boolResult = (bool)lc.Value;
                    }
                    else if (!TryInterpretBool(ref boolResult, left, left.NodeType))
                        return false;

                    // Short circuit the interpretation, because this is an actual logic of these logical operations
                    if (boolResult & nodeType == ExpressionType.OrElse ||
                        !boolResult & nodeType == ExpressionType.AndAlso)
                        return true;

                    // If the first part is not enough to decide of the expression result, go right
                    var right = binaryExpr.Right;
                    if (right is ConstantExpression rc)
                    {
#if LIGHT_EXPRESSION
                        if (rc.RefField != null) return false;
#endif
                        boolResult = (bool)rc.Value;
                        return true;
                    }
                    return TryInterpretBool(ref boolResult, right, right.NodeType);
                }

                if (IsComparison(nodeType))
                {
                    var binaryExpr = (BinaryExpression)expr;
                    var left = binaryExpr.Left;
                    var leftCode = Type.GetTypeCode(left.Type);
                    if (leftCode == TypeCode.Boolean)
                    {
                        var boolLeft = false;
                        if (left is ConstantExpression lc)
                        {
#if LIGHT_EXPRESSION
                            if (lc.RefField != null) return false;
#endif
                            boolLeft = (bool)lc.Value;
                        }
                        else if (!TryInterpretBool(ref boolLeft, left, left.NodeType))
                            return false;

                        var right = binaryExpr.Right;
                        var boolRight = false;
                        if (right is ConstantExpression rc)
                        {
#if LIGHT_EXPRESSION
                            if (rc.RefField != null) return false;
#endif
                            boolRight = (bool)rc.Value;
                        }
                        else if (!TryInterpretBool(ref boolRight, right, right.NodeType))
                            return false;

                        boolResult = nodeType switch
                        {
                            ExpressionType.Equal => boolLeft == boolRight,
                            ExpressionType.NotEqual => boolLeft != boolRight,
                            _ => UnreachableCase(nodeType, false), // todo: @wip we can split IsComparison to check only ExpressionType.Equal and ExpressionType.NotEqual for bool
                        };
                        return true;
                    }

                    if (leftCode == TypeCode.Decimal)
                    {
                        decimal decimalLeft = default;
                        if (left is ConstantExpression lc)
                        {
#if LIGHT_EXPRESSION
                            if (lc.RefField != null) return false;
#endif
                            decimalLeft = (decimal)lc.Value;
                        }
                        else if (!TryInterpretDecimal(ref decimalLeft, left, left.NodeType))
                            return false;

                        var right = binaryExpr.Right;
                        decimal decimalRight = default;
                        if (right is ConstantExpression rc)
                        {
#if LIGHT_EXPRESSION
                            if (rc.RefField != null) return false;
#endif
                            decimalRight = (decimal)rc.Value;
                        }
                        else if (!TryInterpretDecimal(ref decimalRight, right, right.NodeType))
                            return false;

                        boolResult = nodeType switch
                        {
                            ExpressionType.Equal => decimalLeft == decimalRight,
                            ExpressionType.NotEqual => decimalLeft != decimalRight,
                            ExpressionType.GreaterThan => decimalLeft > decimalRight,
                            ExpressionType.GreaterThanOrEqual => decimalLeft >= decimalRight,
                            ExpressionType.LessThan => decimalLeft < decimalRight,
                            ExpressionType.LessThanOrEqual => decimalLeft <= decimalRight,
                            _ => UnreachableCase(nodeType, false),
                        };
                        return true;
                    }
                    // not a bool or decimal
                    {
                        PValue result = default;
                        if (left is ConstantExpression lc)
                        {
#if LIGHT_EXPRESSION
                            if (lc.RefField != null) return false;
#endif
                            UnboxToValue_new(ref result, lc.Value, leftCode);
                        }
                        else if (!TryInterpretPrimitiveValue(ref result, left, left.NodeType))
                            return false;

                        var right = binaryExpr.Right;
                        PValue rightVal = default;
                        if (right is ConstantExpression rc)
                        {
#if LIGHT_EXPRESSION
                            if (rc.RefField != null) return false;
#endif
                            UnboxToValue_new(ref rightVal, rc.Value, leftCode);
                        }
                        else if (!TryInterpretPrimitiveValue(ref rightVal, right, right.NodeType))
                            return false;

                        boolResult = DoComparison_new(ref result, ref rightVal, nodeType);
                        return true;
                    }
                }

                if (expr is ConstantExpression constExpr)
                {
#if LIGHT_EXPRESSION
                    if (constExpr.RefField != null) return false;
#endif
                    boolResult = (bool)constExpr.Value;
                    return true;
                }

                if (nodeType == ExpressionType.Default)
                {
                    boolResult = false;
                    return true;
                }

                return false;
            }

            /// <summary>Tries to interpret the expression of the Primitive type of Constant, Convert, Logical, Comparison, Arithmetic.</summary>
            internal static bool TryInterpretDecimal(ref decimal decimalResult, Expression expr, ExpressionType nodeType)
            {
                // what operations are supported, to get the decimal result?
                // yes: arithmetic, negate, default, convert
                // not: not, logical, comparison

                if (IsArithmeticBinary(nodeType))
                {
                    var binaryExpr = (BinaryExpression)expr;
                    var left = binaryExpr.Left;
                    if (left is ConstantExpression lc)
                    {
#if LIGHT_EXPRESSION
                        if (lc.RefField != null) return false;
#endif
                        decimalResult = (decimal)lc.Value;
                    }
                    else if (!TryInterpretDecimal(ref decimalResult, left, left.NodeType))
                        return false;

                    var right = binaryExpr.Right;
                    decimal decimalRight = default;
                    if (right is ConstantExpression rc)
                    {
#if LIGHT_EXPRESSION
                        if (rc.RefField != null) return false;
#endif
                        decimalRight = (decimal)rc.Value;
                    }
                    else if (!TryInterpretDecimal(ref decimalRight, right, right.NodeType))
                        return false;

                    switch (nodeType)
                    {
                        case ExpressionType.Add: decimalResult += decimalRight; break;
                        case ExpressionType.Subtract: decimalResult -= decimalRight; break;
                        case ExpressionType.Multiply: decimalResult *= decimalRight; break;
                        case ExpressionType.Divide: decimalResult /= decimalRight; break;
                        case ExpressionType.Modulo: decimalResult %= decimalRight; break;
                        default: UnreachableCase(nodeType); break;
                    }
                    return true;
                }

                if (nodeType == ExpressionType.Negate)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        decimalResult = (decimal)co.Value;
                    }
                    else if (!TryInterpretDecimal(ref decimalResult, operandExpr, operandExpr.NodeType))
                        return false;

                    decimalResult = -decimalResult;
                    return true;
                }

                if (expr is ConstantExpression constExpr)
                {
#if LIGHT_EXPRESSION
                    if (constExpr.RefField != null) return false;
#endif
                    decimalResult = (decimal)constExpr.Value;
                    return true;
                }

                if (nodeType == ExpressionType.Default)
                {
                    decimalResult = default;
                    return true;
                }

                if (nodeType == ExpressionType.Convert)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    var operandCode = Type.GetTypeCode(operandExpr.Type);
                    Debug.Assert(operandCode != TypeCode.Boolean, " operand may be a decimal but cannot be bool, because there is no conversation from bool to the types in UValue");

                    PValue operandResult = default;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        UnboxToValue_new(ref operandResult, co.Value, operandCode);
                    }
                    else if (!TryInterpretPrimitiveValue(ref operandResult, operandExpr, operandExpr.NodeType))
                        return false;

                    decimalResult = operandCode switch
                    {
                        TypeCode.Char => operandResult.CharValue,
                        TypeCode.SByte => operandResult.SByteValue,
                        TypeCode.Byte => operandResult.ByteValue,
                        TypeCode.Int16 => operandResult.Int16Value,
                        TypeCode.UInt16 => operandResult.UInt16Value,
                        TypeCode.Int32 => operandResult.Int32Value,
                        TypeCode.UInt32 => operandResult.UInt32Value,
                        TypeCode.Int64 => operandResult.Int64Value,
                        TypeCode.UInt64 => operandResult.UInt64Value,
                        _ => UnreachableCase(operandCode, default(decimal)),
                    };
                    return true;
                }

                return false;
            }

            /// <summary>Tries to interpret the expression of the Primitive type of Constant, Convert, Logical, Comparison, Arithmetic.</summary>
            internal static bool TryInterpretPrimitiveValue(ref PValue result, Expression expr, ExpressionType nodeType)
            {
                // what is supported for the non-boolean, non-decimal result
                // yes: arithmetic, convert, default
                // not: not, logical, comparison

                if (IsArithmeticBinary(nodeType))
                {
                    var binaryExpr = (BinaryExpression)expr;
                    var left = binaryExpr.Left;
                    var leftCode = Type.GetTypeCode(left.Type);
                    if (left is ConstantExpression lc)
                    {
#if LIGHT_EXPRESSION
                        if (lc.RefField != null) return false;
#endif
                        UnboxToValue_new(ref result, lc.Value, leftCode);
                    }
                    else if (!TryInterpretPrimitiveValue(ref result, left, left.NodeType))
                        return false;

                    var right = binaryExpr.Right;
                    PValue rightVal = default;
                    if (right is ConstantExpression rc)
                    {
#if LIGHT_EXPRESSION
                        if (rc.RefField != null) return false;
#endif
                        UnboxToValue_new(ref rightVal, rc.Value, leftCode);
                    }
                    else if (!TryInterpretPrimitiveValue(ref rightVal, right, right.NodeType))
                        return false;

                    DoArithmetic_new(ref result, ref rightVal, nodeType);
                    return true;
                }

                if (nodeType == ExpressionType.Negate)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    if (operandExpr is ConstantExpression co)
                    {
#if LIGHT_EXPRESSION
                        if (co.RefField != null) return false;
#endif
                        UnboxToValue_new(ref result, co.Value, Type.GetTypeCode(co.Type));
                    }
                    else if (!TryInterpretPrimitiveValue(ref result, operandExpr, operandExpr.NodeType))
                        return false;

                    DoNegate_new(ref result);
                    return true;
                }

                if (expr is ConstantExpression constExpr)
                {
#if LIGHT_EXPRESSION
                    if (constExpr.RefField != null) return false;
#endif
                    UnboxToValue_new(ref result, constExpr.Value, Type.GetTypeCode(constExpr.Type));
                    return true;
                }

                if (nodeType == ExpressionType.Default)
                {
                    SetToZero_new(ref result, Type.GetTypeCode(expr.Type));
                    return true;
                }

                if (nodeType == ExpressionType.Convert)
                {
                    var operandExpr = ((UnaryExpression)expr).Operand;
                    var operandCode = Type.GetTypeCode(operandExpr.Type);
                    Debug.Assert(operandCode != TypeCode.Boolean, " operand may be a decimal but cannot be bool, because there is no conversation from bool to the types in UValue");

                    if (operandCode != TypeCode.Decimal)
                    {
                        if (operandExpr is ConstantExpression co)
                        {
#if LIGHT_EXPRESSION
                            if (co.RefField != null) return false;
#endif
                            UnboxToValue_new(ref result, co.Value, operandCode);
                        }
                        else if (!TryInterpretPrimitiveValue(ref result, operandExpr, operandExpr.NodeType))
                            return false;

                        var toCode = Type.GetTypeCode(expr.Type);
                        if (toCode != operandCode)
                            DoConvert_new(ref result, toCode);
                        return true;
                    }
                    // then for the decimal
                    // todo: @perf refactor to the separate method
                    {
                        decimal decimalResult = default;
                        if (operandExpr is ConstantExpression co)
                        {
#if LIGHT_EXPRESSION
                            if (co.RefField != null) return false;
#endif
                            decimalResult = (decimal)co.Value;
                        }
                        else if (!TryInterpretDecimal(ref decimalResult, operandExpr, operandExpr.NodeType))
                            return false;

                        var toCode = Type.GetTypeCode(expr.Type);
                        switch (toCode)
                        {
                            case TypeCode.Char: result.CharValue = (char)decimalResult; break;
                            case TypeCode.SByte: result.SByteValue = (sbyte)decimalResult; break;
                            case TypeCode.Byte: result.ByteValue = (byte)decimalResult; break;
                            case TypeCode.Int16: result.Int16Value = (short)decimalResult; break;
                            case TypeCode.UInt16: result.UInt16Value = (ushort)decimalResult; break;
                            case TypeCode.Int32: result.Int32Value = (int)decimalResult; break;
                            case TypeCode.UInt32: result.UInt32Value = (uint)decimalResult; break;
                            case TypeCode.Int64: result.Int64Value = (long)decimalResult; break;
                            case TypeCode.UInt64: result.UInt64Value = (ulong)decimalResult; break;
                            case TypeCode.Single: result.SingleValue = (float)decimalResult; break;
                            case TypeCode.Double: result.DoubleValue = (double)decimalResult; break;
                            default: UnreachableCase(toCode); break;
                        }
                        result.Code = toCode;
                        return true;
                    }
                }
                return false;
            }
        }
    }

    /// <summary>
    /// Helpers targeting the performance. Extensions method names may be a bit funny (non standard), 
    /// in order to prevent conflicts with YOUR helpers with standard names
    /// </summary>
    public static class Tools
    {
        public static Expression AsExpr(this object obj) => obj as Expression ?? Constant(obj);
        public static Expression[] AsExprs(this object[] obj)
        {
            var exprs = new Expression[obj.Length];
            for (var i = 0; i < obj.Length; i++)
                exprs[i] = obj[i].AsExpr();
            return exprs;
        }

        /// <summary>Returns true if class is compiler generated. Checking for CompilerGeneratedAttribute
        /// is not enough, because this attribute is not applied for classes generated from "async/await".</summary>
        [MethodImpl((MethodImplOptions)256)]
        public static bool IsCompilerGenerated(this Type type) =>
            type.Name[0] == '<'; // consider the types with obstruct names like `<>blah` as compiler-generated

        [MethodImpl((MethodImplOptions)256)]
        internal static bool IsUnsigned(this Type type) =>
            type == typeof(byte) ||
            type == typeof(ushort) ||
            type == typeof(uint) ||
            type == typeof(ulong);

        [MethodImpl((MethodImplOptions)256)]
        internal static bool IsFloatingPoint(this Type type) =>
            type == typeof(float) ||
            type == typeof(double);

        internal static bool IsPrimitiveOrDecimalWithZeroDefault(this Type type)
        {
            switch (Type.GetTypeCode(type))
            {
                case TypeCode.Boolean:
                case TypeCode.Char:
                case TypeCode.SByte:
                case TypeCode.Byte:
                case TypeCode.Int16:
                case TypeCode.UInt16:
                case TypeCode.Int32:
                case TypeCode.UInt32:
                case TypeCode.Int64:
                case TypeCode.UInt64:
                case TypeCode.Single:
                case TypeCode.Double:
                case TypeCode.Decimal:
                    return true;
                // case TypeCode.DateTime:
                // case TypeCode.String:
                default:
                    return false;
            }
        }

        [RequiresUnreferencedCode(Trimming.Message)]
        [MethodImpl((MethodImplOptions)256)]
        public static bool IsNullable(this Type type) =>
            (type.IsValueType & type.IsGenericType) && type.GetGenericTypeDefinition() == typeof(Nullable<>);

        [RequiresUnreferencedCode(Trimming.Message)]
        [MethodImpl((MethodImplOptions)256)]
        public static Type GetUnderlyingNullableTypeOrNull(this Type type) =>
            (type.IsValueType & type.IsGenericType) && type.GetGenericTypeDefinition() == typeof(Nullable<>) ? type.GetGenericArguments()[0] : null;

        [RequiresUnreferencedCode(Trimming.Message)]
        [MethodImpl((MethodImplOptions)256)]
        public static Type GetUnderlyingNullableTypeUnsafe(this Type type) => type.GetGenericArguments()[0];

        [RequiresUnreferencedCode(Trimming.Message)]
        [MethodImpl((MethodImplOptions)256)]
        public static Type GetNonNullableOrSelf(this Type type) => type.IsNullable() ? type.GetGenericArguments()[0] : type;

        [RequiresUnreferencedCode(Trimming.Message)]
        [MethodImpl((MethodImplOptions)256)]
        public static Type GetNullable(this Type type) => typeof(Nullable<>).MakeGenericType(type);

        public static string GetArithmeticBinaryOperatorMethodName(this ExpressionType nodeType) =>
            nodeType switch
            {
                ExpressionType.Add => "op_Addition",
                ExpressionType.AddChecked => "op_Addition",
                ExpressionType.Subtract => "op_Subtraction",
                ExpressionType.SubtractChecked => "op_Subtraction",
                ExpressionType.Multiply => "op_Multiply",
                ExpressionType.MultiplyChecked => "op_Multiply",
                ExpressionType.Divide => "op_Division",
                ExpressionType.Modulo => "op_Modulus",
                _ => null
            };

        internal static bool IsAssignNodeType(this ExpressionType nodeType) => nodeType switch
        {
            ExpressionType.Assign => true,
            ExpressionType.PowerAssign => true,
            ExpressionType.AndAssign => true,
            ExpressionType.OrAssign => true,
            ExpressionType.AddAssign => true,
            ExpressionType.ExclusiveOrAssign => true,
            ExpressionType.AddAssignChecked => true,
            ExpressionType.SubtractAssign => true,
            ExpressionType.SubtractAssignChecked => true,
            ExpressionType.MultiplyAssign => true,
            ExpressionType.MultiplyAssignChecked => true,
            ExpressionType.DivideAssign => true,
            ExpressionType.LeftShiftAssign => true,
            ExpressionType.RightShiftAssign => true,
            ExpressionType.ModuloAssign => true,
            _ => false
        };

        [MethodImpl((MethodImplOptions)256)]
        internal static bool IsBlockLike(this ExpressionType nodeType) =>
            nodeType == ExpressionType.Try |
            nodeType == ExpressionType.Switch |
            nodeType == ExpressionType.Block |
            nodeType == ExpressionType.Loop;

        [MethodImpl((MethodImplOptions)256)]
        internal static bool IsReturnable(this ExpressionType nodeType) =>
            nodeType != ExpressionType.Goto &
            nodeType != ExpressionType.Label &
            nodeType != ExpressionType.Throw &&
            !IsBlockLike(nodeType);

        [MethodImpl((MethodImplOptions)256)]
        internal static bool IsBlockLikeOrConditional(this ExpressionType nodeType) =>
            nodeType == ExpressionType.Conditional | nodeType == ExpressionType.Coalesce ||
            IsBlockLike(nodeType);

        internal static Expression StripConvertRecursively(this Expression expr) =>
            expr is UnaryExpression convert && convert.NodeType == ExpressionType.Convert
                ? StripConvertRecursively(convert.Operand)
                : expr;

        internal static bool IsComplexExpression(this Expression expr)
        {
            expr = expr.StripConvertRecursively();
            return expr.NodeType == ExpressionType.Invoke
                || expr.NodeType.IsBlockLikeOrConditional();
